{
  "ubx_message": {
    "name": "UBX-TIM-VRFY",
    "class_id": "0x0d",
    "message_id": "0x06",
    "message_type": "periodic_polled",
    "description": "Sourced time verification. This message contains verification information about previous time received via assistance data or from RTC.",
    "payload_length": {
      "fixed": 20
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "name": "itow",
        "data_type": "I4",
        "unit": "ms",
        "description": "integer millisecond tow received by source"
      },
      {
        "byte_offset": 4,
        "name": "frac",
        "data_type": "I4",
        "unit": "ns",
        "description": "sub-millisecond part of tow"
      },
      {
        "byte_offset": 8,
        "name": "deltaMs",
        "data_type": "I4",
        "unit": "ms",
        "description": "integer milliseconds of delta time (current time minus sourced time)"
      },
      {
        "byte_offset": 12,
        "name": "deltaNs",
        "data_type": "I4",
        "unit": "ns",
        "description": "Sub-millisecond part of delta time"
      },
      {
        "byte_offset": 16,
        "name": "wno",
        "data_type": "U2",
        "unit": "week",
        "description": "Week number"
      },
      {
        "byte_offset": 18,
        "name": "flags",
        "data_type": "X1",
        "description": "Flags",
        "sub_fields": [
          {
            "bit_offset": 0,
            "num_bits": 3,
            "name": "src",
            "data_type": "U:3",
            "description": "Aiding time source",
            "value_mapping": {
              "0": "no time aiding done",
              "2": "source was RTC",
              "3": "source was assistance data"
            }
          }
        ]
      },
      {
        "byte_offset": 19,
        "name": "reserved0",
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
